import './App.css';
import React from 'react';
import { useState, useEffect } from "react";
import { Card, Space, Button } from 'antd';
import fruitsData from "./fruit.json";
import { Checkbox, Radio} from 'antd';

const onChange = (checkedValues) => {
  console.log('checked = ', checkedValues);
};
const { Meta } = Card;
function App() {
  const[cart, setCart] = useState(new Map()); 
  const[cartTotal, setTotal] = useState(0);
  const season = ["All", "summer", "fall", "winter", "spring"];
  function getFruits() {
    const fruitsList = fruitsData;
    return fruitsList;
  }

  function filterSeason(season) {
    let filtredFruits = getFruits().filter(type => type.season === season);
    return filtredFruits;
  }


  const [filteredFruits, setSeasonFilter] = useState(null);
    useEffect(() => {
      setSeasonFilter(getFruits());
    }, []);

  function handleSeasons(e) {
    let typeSeason = e.target.value;
    console.log(typeSeason)
    typeSeason !== "All"
      ? setSeasonFilter(filterSeason(typeSeason))
      : setSeasonFilter(getFruits());
    console.log(filteredFruits);
  }


  const fruits = filteredFruits && filteredFruits.map((item) => (
    <Card
    style={{
      width: 300,
      margin: 20,
      padding: 0,
    }}
    cover={
      <img
        alt="example"
        src={item.image}
      />
    }
  >
    <Meta
      title={item.name}
    />
    <p>Price: {item.price} </p>
    <p>Season: {item.season} </p> 
    <p>Type: {item.type} </p> 
    <Button onClick={() => addToCart(item)}>Add or Remove</Button>
    </Card>
    ));

  const addToCart = (item) => {
    if (cart.has(item.name)) {
      cart.delete(item.name)
      setTotal(cartTotal - item.price);
    }
    else {
      setCart(cart.set(item.name, item.price));
      setTotal(cartTotal + item.price); 
    }
    console.log(cart)
    };

    const [value, setValue] = useState(1);
    const onChange = (e) => {
      console.log('radio checked', e.target.value);
      setValue(e.target.value);
    };


  return (
    <div className="App">
      <header className="App-header">
        <h1> Fruit Market</h1>
        <h2>Filters</h2> 
        <h3> Sort by </h3>
        <Radio.Group onChange={onChange} value={value}>
          <Radio value={1}>Default</Radio>
          <Radio value={2}>Popularity</Radio>
          <Radio value={3}>Price</Radio>
        </Radio.Group>
        <h3>Seasons</h3>
        {season &&
        season.map((s, index) => (
          <div>
            <Button key={index} value={s} onClick={handleSeasons}>
              {s}
            </Button>
          </div>
        ))}
        {/* <Space>
          <Button value="All" onClick={handleSeasons}>All</Button>
          <Button value="summer" onClick={handleSeasons} >summer</Button>
          <Button value="fall" onClick={handleSeasons}>fall</Button>
          <Button value="winter" onClick={handleSeasons}>winter</Button>
          <Button value="spring" onClick={handleSeasons}>spring</Button>
        </Space> */}

        <h3>Type of Fruit</h3>
        <Space>
          <Button>All</Button>
          <Button>Berries</Button>
          <Button>Tropical</Button>
          <Button>Other</Button>
        </Space>

        <h3> Other </h3>
        <Checkbox onChange={onChange}>In Cart</Checkbox>
      </header>
      <div id="grid"> 
      <Space wrap> 
        {fruits}
      </Space>
      <div id="cart">

        <h2>Cart</h2>
        {
          Array.from(cart, ([item, count]) => <p>{count} x {item}</p>)
        }
        Total: {cartTotal}
      </div>
      </div>
    </div>
  );
}

export default App;
