import './App.css';
import React from 'react';
import { useState } from "react";
import { Card, Space, Button } from 'antd';
import fruitsData from "./fruit.json";
import { Checkbox } from 'antd';

const onChange = (checkedValues) => {
  console.log('checked = ', checkedValues);
};
const { Meta } = Card;
const seasons = ['Summer', 'Fall', 'Winter', 'Spring', 'All'];
function App() {

  const[cart, setCart] = useState(new Map()); 
  const[cartTotal, setTotal] = useState(0);

  const fruits = fruitsData.map((item) => (
    <Card
    style={{
      width: 300,
      margin: 20,
      padding: 0,
    }}
    cover={
      <img
        alt="example"
        src={item.image}
      />
    }
  >
    <Meta
      title={item.name}
    />
    <p>Price: {item.price} </p>
    <p>Season: {item.season} </p> 
    <p>Type: {item.type} </p> 
    <Button onClick={() => addToCart(item)}>Add or Remove</Button>
    </Card>
    ));

  const addToCart = (item) => {
    if (cart.has(item.name)) {
      cart.delete(item.name)
      setTotal(cartTotal - item.price);
    }
    else {
      setCart(cart.set(item.name, item.price));
      setTotal(cartTotal + item.price); 
    }
    console.log(cart)
    };

  return (
    <div className="App">
      <header className="App-header">
        <h1> Fruit Market</h1>
      </header>
      <div id="grid"> 
      <Space wrap> 
        {fruits}
      </Space>
      <div id="cart">
        <h2>Filters</h2> 
        <Space wrap>
          <Checkbox.Group wrap options={seasons} defaultValue={['All']} onChange={onChange} />
        </Space>
        <h2>Cart</h2>
        {
          Array.from(cart, ([item, count]) => <p>{count} x {item}</p>)
        }
        Total: {cartTotal}
      </div>
      </div>
    </div>
  );
}

export default App;
